html:
{% set first_aya = ayat|first.aya %}
{% set last_aya = ayat|last.aya %}
<p class='quran-wrapper'>
{% for aya in ayat %}
<span class='quran-arabic-text'>{{aya.text}}</span>
<span class='quran-aya-number'>{{aya.aya}}</span>
<span class='quran-translation-text'>{{aya.translation}}</span>
{% endfor %}
<span class='quran-source-name'>{{sura.name_transliteration}}, 
{{first_aya}}
{% if first_aya < last_aya %}
 - {{last_aya}}
{% endif %}
</span>
</p>

css:
@font-face {
  font-family: "Scheheraza";
  src: url("Scheherazade.eot");
  src: local("Scheherazade"), url("Scheherazade.ttf") format("truetype"); 
}

/* Quranic text */
.quran-wrapper {
  position: relative;
  margin: 0 10% 26px 10%; 
}

.quran-arabic-text {
  display: block;
  direction: rtl;
  font-family: Scheherazade, Scheheraza;
  font-size: 1.75rem; 
}

.quran-aya-number {
  display: inline;
  margin-right: 3px; 
}
  .quran-aya-number:before {
    content: "("; 
  }
  .quran-aya-number:after {
    content: ") "; 
  }

.quran-translation-text {
  display: inline; 
}

.quran-source-name {
  display: block;
  font-style: italic;
  text-align: right;
  margin: 0; 
}

html:
{% set first_aya = ayat|first.aya %}
{% set last_aya = ayat|last.aya %}
<p class='quran-arabic'>
	{% for aya in ayat %}
	<span class='text'>{{aya.text}}</span>
	<span class='aya-number'>{{aya.aya|arabic}}</span>
	{% endfor %}
</p>
<p class='quran-translation'>
	{% for aya in ayat %}
	<span class='aya-number'>{{aya.aya}}</span>
	<span class='text'>{{aya.translation}}</span>
	{% endfor %}
</p>
<p class='quran-source-name'>{{sura.name_transliteration}}, {{first_aya}}
{% if first_aya < last_aya %}
 - {{last_aya}}
{% endif %}
</p>

css:
/* Quranic text */

@font-face {
    font-family: 'me_quranR3HOQ';
    src: url('me_quranR3HOQ.ttf') format('truetype');
}

.quran-arabic{
  font-family: me_quranR3HOQ;
  font-size: 14px !important; 
  margin: auto 10%;
  line-height: 200% !important;
  text-align: right;
  direction: rtl;

}
.quran-arabic .aya-number {
	margin: 0 0.5em;
	font-size: 14px;
	direction: rtl;
}
.quran-arabic .aya-number:before{
	content: "﴿";
}

.quran-arabic .aya-number:after {
	content: "﴾";	
}

.quran-translation {
	margin: auto 10%;
	font-size: 14px;
}
.quran-translation .aya-number:before{
	content: "(";
}

.quran-translation .aya-number:after {
	content: ")";	
}


.quran-source-name {
	text-align: right;
	font-style: italic;

}
